namespace helper {
    /**
     * Class used for testing propose.
     * It's a friend class. Grands access to private functions.
    */
    class Tests final {
    public:
        // pass tested value.
        // side effect: change @bigInt
        Tests(BigInt* bigInt) :
            m_bigInt(bigInt)
        {
        }

        // Main tested functions
        const BigInt& AddPositiveInteger(const BigInt& rhs) {
            return m_bigInt->AddPositiveInteger(rhs);
        }

        const BigInt& SubstructSmallerPositiveInteger(const BigInt& rhs) {
            return m_bigInt->SubstructSmallerPositiveInteger(rhs);
        }
        
    private:
        // tested value
        BigInt* m_bigInt;
    };
}



